de + 1, start, mid, l, r); int rightSum = query(2 * node + 2, mid + 1, end, l, r); return leftSum + rightSum; } // å•ç‚¹æ›´æ–° public void update(int index, int val) { update(0, 0, n - 1, index, val); } private void update(int node, int start, int end, int index, int val) { if (start == end) { tree[node] = val; return; } int mid = start + (end - start) / 2; int leftNode = 2 * node + 1; int rightNode = 2 * node + 2; if (index &lt;= mid) { update(leftNode, start, mid, index, val); } else { update(rightNode, mid + 1, end, index, val); } tree[node] = tree[leftNode] + tree[rightNode]; } } // ä½¿ç”¨ç¤ºä¾‹ int[] nums = {1, 3, 5, 7, 9, 11}; SegmentTree segTree = new SegmentTree(nums); int sum = segTree.query(1, 3); // æŸ¥è¯¢ [1,3] åŒºé—´å’Œ segTree.update(1, 10); // æ›´æ–° index=1 çš„å€¼ä¸º 10 3. å›¾ç®—æ³• å›¾çš„è¡¨ç¤º // é‚»æ¥è¡¨ Map&lt;Integer, List&lt;Integer&gt;&gt; graph = new HashMap&lt;&gt;(); // æˆ– List&lt;List&lt;Integer&gt;&gt; graph = new ArrayList&lt;&gt;(); // é‚»æ¥çŸ©é˜µ int[][] graph = new int[n][n]; DFS - å›¾çš„æ·±åº¦ä¼˜å…ˆéå† void dfs(int node, Set&lt;Integer&gt; visited, List&lt;List&lt;Integer&gt;&gt; graph) { visited.add(node); System.out.println(node); for (int neighbor : graph.get(node)) { if (!visited.contains(neighbor)) { dfs(neighbor, visited, graph); } } } // ä½¿ç”¨ Set&lt;Integer&gt; visited = new HashSet&lt;&gt;(); dfs(0, visited, graph); BFS - å›¾çš„å¹¿åº¦ä¼˜å…ˆéå† void bfs(int start, List&lt;List&lt;Integer&gt;&gt; graph) { Set&lt;Integer&gt; visited = new HashSet&lt;&gt;(); Queue&lt;Integer&gt; queue = new LinkedList&lt;&gt;(); queue.offer(start); visited.add(start); while (!queue.isEmpty()) { int node = queue.poll(); System.out.println(node); for (int neighbor : graph.get(node)) { if (!visited.contains(neighbor)) { queue.offer(neighbor); visited.add(neighbor); } } } } æ‹“æ‰‘æ’åºï¼ˆKahn ç®—æ³•ï¼‰ List&lt;Integer&gt; topologicalSort(int n, int[][] edges) { List&lt;List&lt;Integer&gt;&gt; graph = new ArrayList&lt;&gt;(); int[] inDegree = new int[n]; for (int i = 0; i &lt; n; i++) { graph.add(new ArrayList&lt;&gt;()); } for (int[] edge : edges) { graph.get(edge[0]).add(edge[1]); inDegree[edge[1]]++; } Queue&lt;Integer&gt; queue = new LinkedList&lt;&gt;(); for (int i = 0; i &lt; n; i++) { if (inDegree[i] == 0) { queue.offer(i); } } List&lt;Integer&gt; res = new ArrayList&lt;&gt;(); while (!queue.isEmpty()) { int node = queue.poll(); res.add(node); for (int neighbor : graph.get(node)) { inDegree[neighbor]--; if (inDegree[neighbor] == 0) { queue.offer(neighbor); } } } return res.size() == n ? res : new ArrayList&lt;&gt;(); } Dijkstra æœ€çŸ­è·¯å¾„ç®—æ³• int[] dijkstra(int n, int[][] edges, int start) { // æ„å»ºé‚»æ¥è¡¨: {neighbor, weight} List&lt;List&lt;int[]&gt;&gt; graph = new ArrayList&lt;&gt;(); for (int i = 0; i &lt; n; i++) { graph.add(new ArrayList&lt;&gt;()); } for (int[] edge : edges) { graph.get(edge[0]).add(new int[]{edge[1], edge[2]}); } int[] dist = new int[n]; Arrays.fill(dist, Integer.MAX_VALUE); dist[start] = 0; // ä¼˜å…ˆé˜Ÿåˆ—: {distance, node} PriorityQueue&lt;int[]&gt; pq = new PriorityQueue&lt;&gt;((a, b) -&gt; a[0] - b[0]); pq.offer(new int[]{0, start}); while (!pq.isEmpty()) { int[] curr = pq.poll(); int d = curr[0], node = curr[1]; if (d &gt; dist[node]) continue; for (int[] neighbor : graph.get(node)) { int next = neighbor[0], weight = neighbor[1]; int newDist = d + weight; if (newDist &lt; dist[next]) { dist[next] = newDist; pq.offer(new int[]{newDist, next}); } } } return dist; } Bellman-Ford ç®—æ³•ï¼ˆå¯å¤„ç†è´Ÿæƒè¾¹ï¼‰ int[] bellmanFord(int n, int[][] edges, int start) { int[] dist = new int[n]; Arrays.fill(dist, Integer.MAX_VALUE); dist[start] = 0; // æ¾å¼› n-1 æ¬¡ for (int i = 0; i &lt; n - 1; i++) { for (int[] edge : edges) { int u = edge[0], v = edge[1], w = edge[2]; if (dist[u] != Integer.MAX_VALUE &amp;&amp; dist[u] + w &lt; dist[v]) { dist[v] = dist[u] + w; } } } // æ£€æµ‹è´Ÿç¯ for (int[] edge : edges) { int u = edge[0], v = edge[1], w = edge[2]; if (dist[u] != Integer.MAX_VA